				["custom"] = "function(allstates,e,...)\n    --Get the current timestamp\n    local timestamp = GetTime()\n    --First spawns 12 sec in, then every 46 sec\n    --These can be modified if better data is found\n    --Also you can change the boss emote string to search for\n    local first,interval,search\n    first = 12\n    interval = 46\n    search = \"Kill the Imp Servants\"\n    --Remove all the old states\n    for _,v in pairs(allstates) do\n        v.show = false\n    end\n    --Initialize a state table\n    local state = {}\n    --Set some default state values\n    state.changed = true\n    state.show = true\n    state.name = \"Healing Imps\"\n    state.icon = nil\n    state.stacks = 1\n    state.progressType = \"timed\"\n    state.autoHide = true\n    --initialize an insert boolean\n    local insert\n    if e == \"PLAYER_REGEN_DISABLED\" then\n        --We just entered combat\n        if UnitName(\"boss1\") == \"Agatha\" then\n            --If we entered combat and the boss is agatha, trigger\n            --Reset the table, and add one entry for the timer\n            --this entry is set to the first set\n            state.duration = first\n            state.expirationTime = timestamp + first\n            insert = true\n        end\n    elseif e == \"RAID_BOSS_EMOTE\" then\n        --There was a chat message, we might need to reset\n        local msg = ...\n        if msg then\n            --make sure we actually got a message\n            if string.match(msg, search) then\n                --Imps just spawned, reset timer to new interval\n                --Reset the table, and add one entry for the timer\n                --this entry is set to the new interval\n                state.duration = interval\n                state.expirationTime = timestamp + interval\n                insert = true\n            end\n        end\n    elseif e == \"PLAYER_REGEN_ENABLED\" or e == \"PLAYER_DEAD\" then\n        --Left combat or died, reset the whole thing\n        insert = false\n    end\n    --Place the new state in the allstates table, if we determined we need it\n    if insert == true then\n        local i = 1\n        while allstates[i] do\n            i = i +1\n        end\n        allstates[i] = state\n    end\n    return true\nend",
