				["customIcon"] = "function ()\n    return GetSpellTexture( aura_env.recommended )\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
				["customTexture"] = "\n\n",
				["custom_hide"] = "timed",
				["events"] = "UNIT_POWER_FREQUENT, SPELL_UPDATE_COOLDOWN, SPELL_UPDATE_CHARGES, PLAYER_TARGET_CHANGED, UNIT_SPELLCAST_SUCCEEDED",
				["check"] = "update",
