of the GCD.\n    now = max( now, gcd )\n    \n    -- if the GCD isn't active, calculate what the GCD should be.\n    if gcdDuration == 0 then\n        gcdDuration = max( 1, 1.5 / ( 1 + ( GetHaste() / 100 ) ) )\n    end\n    \n    -- Grab resource info.\n    local holy_power = UnitPower( \"player\", SPELL_POWER_HOLY_POWER )\n    local holy_power_max = UnitPowerMax(\" player\", SPELL_POWER_HOLY_POWER )\n    local holy_power_deficit = holy_power_max - holy_power\n    \n    local target_hp = UnitHealth( 'target' ) or 100\n    local target_hp_max = UnitHealthMax( 'target' ) or 100    \n    if target_hp_max <= 0 then target_hp_max = max( 100, target_hp ) end    \n    local target_hp_pct = target_hp * 100 / target_hp_max\n    \n    -- Get active talents.\n    for k,v in pairs( talentList ) do\n        talented[ k ] = select(4, GetTalentInfo( unpack( v ) ) )\n        if treatAsCD[ k ] and not WA_MiniHekili_Ret_CDs then talented[ k ] = false end\n    end\n    \n    -- Grab ability CDs.\n    for k,v in pairs( abilities ) do\n        local start, duration = GetSpellCooldown(v)\n        cooldowns[ k ] = IsUsableSpell(v) and max( 0, start + duration - now ) or 999\n        if treatAsCD[ k ] and not WA_MiniHekili_Ret_CDs then cooldowns[ k ] = 999 end\n    end\n    \n    local artifact = IsEquippedItem( 120978 )\n    if not artifact then cooldowns.wake_of_ashes = 999 end\n    if talented.zeal then cooldowns.crusader_strike = 999 end\n    if talented.crusade then cooldowns.avenging_wrath = 999 end\n    \n    -- Check # of charges.\n    for k,v in pairs( chargedAbilities ) do\n        local c, maxCharges, start, duration = GetSpellCharges(v)\n        \n        charges[ k ] = min( maxCharges, c + ( max(0, 1 - ( start + duration - now) / duration ) ) )\n        chargesMax[ k ] = maxCharges\n        chargeTime[ k ] = duration\n        \n        if treatAsCD[ k ] and not WA_MiniHekili_Ret_CDs then charges[ k ] = 0 end\n    end\n    \n    -- Check if buffs are up.\n    for k,v in pairs( 